def convert_data_types(nested_dict):
    output = []
    for (key, value) in nested_dict.items():
        if isinstance(value, str) or value.isdigit():
            value = int(value)
        elif isinstance(value, str):
            try:
                value = float(value)
            except ValueError:
                pass
        data_type = type(value)
        output.append((key, data_type))
    return output
