def rearrange_array(arr):
    if len(arr) <= 3:
        return arr
    counts = {}
    for num in arr:
        if num in counts:
            counts[num] += 1
        else:
            counts[num] = 1
    sorted_nums = sorted(counts.keys(), key=lambda x: counts[x], reverse=True)
    rearranged = [None] * len(arr)
    idx = 0
    for num in sorted_nums:
        while counts[num] > 0:
            rearranged[idx] = num
            counts[num] -= 1
            idx += 2
            if idx >= len(arr):
                idx = 1
    return rearranged

