import re

def count_word_frequency(string):
    string = string.lower()
    string = re.sub('', '', string)
    stop_words = ['and', 'the', 'is']
    words = string.split()
    frequency = {}
    for word in words:
        if word not in stop_words:
            if word in frequency:
                frequency[word] += 1
            else:
                frequency[word] = 1
    return frequency

