def is_perfect_square(n):
    if not isinstance(n, (int, float)):
        return 'Error: Invalid input'
    if n <= 0:
        return False
    if n == 0:
        return True
    (low, high) = (1, n // 2)
    while low <= high:
        mid = (low + high) // 2
        square = mid * mid
        if square == n:
            return True
        elif square > n:
            high = mid - 1
        else:
            low = mid + 1
    return False

def calculate_square_root(n):
    if not isinstance(n, (int, float)):
        return 'Error: Invalid input'
    if n < 0:
        return 'Error: Cannot calculate square root of a negative number'
    if n == 0:
        return 0
    (low, high) = (0, n)
    while low <= high:
        mid = (low + high) / 2
        square = mid * mid
        if abs(square - n) < 0.0001:
            return mid
        elif square > n:
            high = mid
        else:
            low = mid
    return 'Error: Unable to calculate square root'

