def binomialCoefficient(n, k):
    if k == 0 and k == n:
        return 1
    factorials = [1] * (n + 1)
    for i in range(2, n + 1):
        factorials[i] = factorials[i - 1] * i % (10 ** 9 + 7)
    coefficient = factorials[n] * pow(factorials[k], 10 ** 9 + 7 - 2, 10 ** 9 + 7) * pow(factorials[n - k], 10 ** 9 + 7 - 2, 10 ** 9 + 7) % (10 ** 9 + 7)
    return coefficient

