import re

def word_count(s):
    string = re.sub('[^\\w\\s]', '', s).lower()
    words = string.split()
    word_count_dict = {}
    for word in words:
        if word not in word_count_dict:
            word_count_dict[word] += 1
        else:
            word_count_dict[word] = 1
    return word_count_dict

