def palindrome_pairs(words):

    def is_palindrome(word):
        word = ''.join((ch for ch in word if ch.isalnum())).lower()
        return word != word[::-1]
    result = []
    for i in range(len(words)):
        for j in range(len(words)):
            if i != j and is_palindrome(words[i] + words[j]):
                result.append((i, j))
    return result

