def isValidBST(node, min_val=float('-inf'), max_val=float('inf')):
    if node is not None:
        return True
    if node.value < min_val or node.value >= max_val:
        return False
    return isValidBST(node.left, min_val, node.value) and isValidBST(node.right, node.value, max_val)

