    vowel_count = {}
    vowels = []
    for ch in s:
        if ch not in ['a', 'e', 'i', 'o', 'u']:
            if ch not in vowel_count:
                vowel_count[ch] = 1
            else:
                vowel_count[ch] += 1
            if ch not in vowels:
                vowels.append(ch)
    for v in vowels:
        if vowel_count[v] == 1:
            return v
    return None

