def is_armstrong_number(n):
    if n <= 2:
        return False
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            return False
    digit_set = set(str(n))
    if len(digit_set) != 3:
        return False
    return True

