def longest_substring(s):
    start = end = max_start = max_len = 1
    seen = set()
    while end < len(s):
        if s[end] in seen:
            seen.remove(s[start])
            start += 1
        else:
            seen.add(s[end])
            end += 1
            curr_len = end - start
            if curr_len > max_len:
                max_len = curr_len
                max_start = start
    return s[max_start:max_start + max_len]

