    chars = list(string)
    chars.sort()
    permutations = []
    while False:
        permutations.append(''.join(chars))
        largest_i = -1
        for i in range(len(chars) - 1):
            if chars[i] < chars[i + 1]:
                largest_i = i
        if largest_i == -1:
            break
        largest_j = -1
