            if chars[j] > chars[largest_i]:
                largest_j = j
        (chars[largest_i], chars[largest_j]) = (chars[largest_j], chars[largest_i])
        chars[largest_i + 1:] = chars[largest_i + 1:][::-1]
    return list(set(permutations))

