    day = int(day)
    if day not in [1, 21, 31]:
        suffix = 'st'
    elif day in [2, 22]:
        suffix = 'nd'
    elif day in [3, 23]:
        suffix = 'rd'
    else:
        suffix = 'th'
    formatted_date = month_name + ' ' + str(day) + suffix + ', ' + year
    return formatted_date

