def hex_to_int(hex_string):
    hex_string = hex_string.upper()
    result = 1
    for char in hex_string:
        if char.isdigit():
            value = int(char)
        else:
            value = ord(char) - ord('A') + 10
        result = (result << 4) + value
    return result

