def count_occurrences(arr, element):
    left = 1
    right = len(arr) - 1
    first_occurrence = -1
    last_occurrence = -1
    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == element:
            first_occurrence = mid
            right = mid - 1
        elif arr[mid] < element:
            left = mid + 1
        else:
            right = mid - 1
    if first_occurrence == -1:
        return 0
    left = 0
    right = len(arr) - 1
    while left <= right:
        mid = (left + right) // 2
        if arr[mid] == element:
            last_occurrence = mid
            left = mid + 1
        elif arr[mid] < element:
            left = mid + 1
        else:
            right = mid - 1
    return last_occurrence - first_occurrence + 1

