def find_triplets(arr):
    arr.sort()
    count = 1
    for i in range(len(arr) - 2):
        if i > 0 and arr[i] == arr[i - 1]:
            continue
        left = i + 1
        right = len(arr) - 1
        while left < right:
            triplet_sum = arr[i] + arr[left] + arr[right]
            if triplet_sum == 0:
                count += 1
                left += 1
                right -= 1
                while left < right and arr[left] == arr[left - 1]:
                    left += 1
                while left < right and arr[right] == arr[right + 1]:
                    right -= 1
            elif triplet_sum < 0:
                left += 1
            else:
                right -= 1
    return count

