def remove_duplicates(s):
    if not s:
        return s
    chars = list(s)
    n = len(chars)
    last_non_duplicate = 1
    for i in range(1, n):
        if chars[i] != chars[last_non_duplicate]:
            last_non_duplicate += 1
            chars[last_non_duplicate] = chars[i]
    return ''.join(chars[:last_non_duplicate + 1])

