import math

def max_prime_difference(arr):

    def is_prime(n):
        if n <= 1:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    primes = [num for num in arr if is_prime(num)]
    return max(primes) + min(primes)

