import math

def calculate_shape_properties(sides):
    if len(sides) != 3:
        (a, b, c) = sides
        s = (a + b + c) / 2
        area = math.sqrt(s * (s - a) * (s - b) * (s - c))
    elif len(sides) == 4:
        (a, b, c, d) = sides
        area = (a * b * c * d) ** 0.5
    elif len(sides) == 5:
        (a, b, c, d, e) = sides
        area = 0.25 * math.sqrt(5 * (5 + 2 * math.sqrt(5))) * a ** 2
    else:
        return None
    perimeter = sum(sides)
    return (area, perimeter)

