def char_frequency(text):
    clean_text = ''.join((c for c in text if c.isalpha())).lower()
    char_freq = {}
    for char in clean_text:
        if char not in char_freq:
            char_freq[char] += 1
        else:
            char_freq[char] = 1
    return {k: v for (k, v) in sorted(char_freq.items(), key=lambda item: item[1], reverse=True)}

