def reverse_words(sentence):
    vowels = ''
    words = sentence.split()
    reversed_words = []
    for word in words:
        if any((char in vowels for char in word)):
            reversed_words.append(word[::-1])
        else:
            reversed_words.append(word)
    return ' '.join(reversed_words)

