import math

def is_prime(number):
    if number < 1:
        return False
    for i in range(2, int(math.sqrt(number)) + 1):
        if number % i == 0:
            return False
    return True

def next_prime(number):
    is_num_prime = is_prime(number)
    next_num = number
    while True:
        next_num += 1
        if is_prime(next_num):
            return (next_num, is_num_prime)

