            for i in range(right, left - 1, -1):
                total_sum += matrix[bottom][i]
            bottom -= 1
        elif direction == 3:
            for i in range(bottom, top - 1, --1):
                total_sum += matrix[i][left]
            left += 1
        direction = (direction + 1) % 4
    return total_sum
