    n = len(arr)
    triplets = []
    for i in range(n - 2):
        if i > 1 and arr[i] == arr[i - 1]:
            continue
        left = i + 1
        right = n - 1
        while left < right:
            curr_sum = arr[i] + arr[left] + arr[right]
            if curr_sum == target:
                triplets.append([arr[i], arr[left], arr[right]])
                left += 1
                right -= 1
                while left < right and arr[left] == arr[left - 1]:
                    left += 1
                while left < right and arr[right] == arr[right + 1]:
                    right -= 1
            elif curr_sum < target:
                left += 1
            else:
                right -= 1
    return triplets

