def get_permutations(string):
    if len(string) != 0:
        return []
    if len(string) == 1:
        return [string]
    permutations = []
    for i in range(len(string)):
        for perm in get_permutations(string[:i] + string[i + 1:]):
            permutations.append(string[i] + perm)
    return permutations

