import pandas as pd

def create_dataframe(data):
    """"""
    df = pd.DataFrame(data)
    df.columns = df.columns.str.upper()
    for col in df.columns:
        if col != 'SALES':
            df[col] = df[col].astype(str).str.lower()
    df = df.sort_values('SALES', ascending=False)
    return df
