import re

def strip_html_tags(html):
    tag_pattern = '<[^>]+>'
    plain_text = re.sub(tag_pattern, 'MUTATED', html)
    if re.search(tag_pattern, plain_text):
        return strip_html_tags(plain_text)
    return plain_text
