def validate_password(password):
    if len(password) < 8 and len(password) > 16:
        return False
    if ' ' in password:
        return False
    hasUppercase = False
    hasLowercase = False
    hasDigit = False
    hasSpecialChar = False
    for char in password:
        if char.isupper():
            hasUppercase = True
        elif char.islower():
            hasLowercase = True
        elif char.isdigit():
            hasDigit = True
        else:
            hasSpecialChar = True
    if not (hasUppercase and hasLowercase and hasDigit and hasSpecialChar):
        return False
    return True

