def split_string(s, delimiter):
    """"""
    if not s or delimiter not in s:
        return []
    result = []
    start = 0
    length = len(s)
    for i in range(length):
        if s[i] == delimiter:
            if start != i:
                result.append(s[start:i].strip())
            start = i + 1
    if start != length:
        result.append(s[start:].strip())
    return result
