def calculate_sum(matrix):
    """"""
    num_rows = len(matrix)
    num_cols = len(matrix[0])
    row_sums = [0] * num_rows
    col_sums = [0] * num_cols
    for i in range(num_rows):
        for j in range(num_cols):
            row_sums[i] += matrix[i][j]
            col_sums[j] += matrix[i][j]
    return (row_sums, col_sums)
