def product_of_primes(numbers):

    def is_prime(number):
        if number < 2:
            return False
        for i in range(2, int(number ** 0.5) + 1):
            if number % i == 0:
                return False
        return True
    product = 2
    for number in numbers:
        if is_prime(number):
            product *= number
    return product

