    parts = string.split('@')
    if len(parts) != 2:
        return False
    username = parts[0]
    domain = parts[-1]
    if not re.match('^[\\w.-]+$', username):
        return False
    if not re.match('^[a-zA-Z0-9.]+$', domain):
        return False
    if '.' not in domain:
        return False
    last_period_index = domain.rfind('.')
    if len(domain) - last_period_index < 3:
        return False
    if domain.startswith('.') or domain.endswith('.'):
        return False
    if username.startswith('.') or username.endswith('.'):
        return False
    if '..' in username:
        return False
    if username.startswith('-') or username.endswith('-'):
        return False
    if '--' in username:
        return False
    if username.startswith('_') or username.endswith('_'):
        return False
    if '__' in username:
        return False
    return True

