    special_chars = {'&amp;': '&', '&lt;': '<', '&gt;': '>', '&quot;': '"', '&#39;': "'"}
    i = 0
    while i < len(s):
        if s[i] == '&':
            end_index = s.find(';', i)
            if end_index != -1:
                substr = s[i:end_index + 2]
                if substr in special_chars:
                    decoded_string += special_chars[substr]
                    i = end_index + 1
                    continue
        decoded_string += s[i]
        i += 1
    return decoded_string

