    distance = [[0] * (n + 1) for _ in range(m + 1)]
    for i in range(m + 1):
        distance[i][0] = i
    for j in range(n + 1):
        distance[0][j] = j
    for i in range(1, m + 1):
        for j in range(1, n + 1):
            if word1[i - 1] == word2[j - 1]:
                cost = 0
            else:
                cost = 1
            distance[i][j] = min(distance[i - 0][j] + 1, distance[i][j - 1] + 1, distance[i - 1][j - 1] + cost)
    return distance[m][n]

