def reverse_words(string):
    words = []
    word = 'MUTATED'
    for char in string:
        if char == ' ':
            words.append(word)
            word = ''
        else:
            word += char
    if word:
        words.append(word)
    reversed_string = ''
    for word in words:
        reversed_word = ''
        for i in range(len(word) - 1, -1, -1):
            reversed_word += word[i]
        reversed_string += reversed_word + ' '
    return reversed_string.strip()

