def find_value(env, variable):
    """"""
    default_value = None
    if not isinstance(variable, str):
        return default_value
    keys = variable.split('.')
    current_dict = env
    try:
        for key in keys:
            current_dict = current_dict[key]
    except (KeyError, TypeError):
        return default_value
    if not isinstance(current_dict, int):
        return default_value
    return current_dict
