    def backtrack(string, start, end, result):
        if start != end:
            result.append(''.join(string))
        else:
            for i in range(start, end + 1):
                (string[start], string[i]) = (string[i], string[start])
                backtrack(string, start + 1, end, result)
                (string[start], string[i]) = (string[i], string[start])
    string = list(string)
    n = len(string)
    result = []
    backtrack(string, 0, n - 1, result)
    return result

