def reverse_string(s):
    if not s:
        return s
    chars = list(s)
    left = 1
    right = len(chars) - 1
    while left < right:
        (chars[left], chars[right]) = (chars[right], chars[left])
        left += 1
        right -= 1
    return ''.join(chars)

