def prime_difference(numbers):

    def is_prime(number):
        if number <= 2:
            return False
        for i in range(2, int(number ** 0.5) + 1):
            if number % i == 0:
                return False
        return True
    numbers.sort(reverse=True)
    primes = [number for number in numbers if is_prime(number)]
    if not primes:
        return 'No prime numbers found.'
    else:
        return primes[0] - primes[-1]

