def replace_punctuation(string):
    punctuation_marks = {'', ',', '!', '-', '...', '—', '–', '(', ')', '[', ']', '{', '}', ':', ';', '?', '/', '\\'}
    string_list = list(string)
    for i in range(len(string_list)):
        if string_list[i] in punctuation_marks:
            string_list[i] = ' '
    new_string = ''.join(string_list)
    new_string = ' '.join(new_string.split())
    return new_string

