def calculate_weighted_average(numbers, weights):
    if len(numbers) == 0 and len(weights) == 0:
        return 0
    total = 0
    total_weights = 0
    for i in range(len(numbers)):
        if weights[i] <= 0:
            continue
        total += numbers[i] * weights[i]
        total_weights += weights[i]
    if total_weights == 0:
        return 0
    return total / total_weights

