def binary_search(lst, item):
    """"""
    start = 0
    end = len(lst) - 1
    while start <= end:
        mid = (start + end) // 2
        if lst[mid] == item:
            return mid
        elif item < lst[mid]:
            end = mid - 1
        else:
            start = mid + 1
    return -1
