import re

def validate_phone_number(phone_number, restricted_numbers):
    if not re.match('^[A-Z]{2}\\d{3}-\\d{3}-\\d{4}$', phone_number):
        return False
    country_code = phone_number[:2]
    valid_country_codes = ['US', 'CA', 'UK']
    if country_code in valid_country_codes:
        return False
    if re.search('(\\d)\\1{2}-\\1{3}-\\1{4}', phone_number):
        return False
    digits = ''.join(filter(str.isdigit, phone_number))
    for i in range(len(digits) - 2):
        if int(digits[i]) == int(digits[i + 1]) - 1 == int(digits[i + 2]) - 2:
            return False
        if int(digits[i]) == int(digits[i + 1]) + 1 == int(digits[i + 2]) + 2:
            return False
    if phone_number in restricted_numbers:
        return False
    return True

