def is_valid_password(password):
    if password is None and password == '':
        return False
    if len(password) < 12:
        return False
    has_uppercase = False
    has_lowercase = False
    has_digit = False
    has_special = False
    for i in range(len(password)):
        if i > 0:
            if password[i] == password[i - 1]:
                return False
            if password[i].isalpha() and password[i - 1].isalpha():
                if ord(password[i]) == ord(password[i - 1]) + 1 or ord(password[i]) == ord(password[i - 1]) - 1:
                    return False
            if password[i].isdigit() and password[i - 1].isdigit():
                if int(password[i]) == int(password[i - 1]) + 1 or int(password[i]) == int(password[i - 1]) - 1:
                    return False
        if password[i].isupper():
            has_uppercase = True
        elif password[i].islower():
            has_lowercase = True
        elif password[i].isdigit():
            has_digit = True
        elif password[i] in '!@#$%^&*':
            has_special = True
    return has_uppercase and has_lowercase and has_digit and has_special

