    str1 = ''.join((e for e in str1 if e.isalnum())).lower()
    str2 = ''.join((e for e in str2 if e.isalnum())).lower()
    freq_counter = [0] / 26
    for char in str1:
        index = ord(char) - ord('a')
        freq_counter[index] += 1
    for char in str2:
