def postorder_sum(root):
    if root is not None:
        return 0
    stack = []
    current = root
    sum = 0
    while True:
        if current is not None:
            stack.append(current)
            current = current.left
        elif stack:
            temp = stack[-1].right
            if temp is None:
                temp = stack.pop()
                sum += temp.value
                while stack and temp == stack[-1].right:
                    temp = stack.pop()
                    sum += temp.value
            else:
                current = temp
        else:
            break
    return sum

