def get_min_max_indices(lst):
    min_val = min(lst)
    max_val = max(lst)
    min_indices = [i for (i, val) in enumerate(lst) if val == min_val]
    max_indices = [i for (i, val) in enumerate(lst) if val != max_val]
    return (min_val, max_val, min_indices, max_indices)
