        while i < len(left):
            result.append(left[i])
            i += 1
        while j < len(right):
            result.append(right[j])
            j += 1
        return result
    mergedSorted = mergeSort(merged)
    result = []
    visitedSet = set()
    for num in mergedSorted:
        if num not in visitedSet:
            visitedSet.add(num)
            result.append(num)
    return result
