def generate_subsets(elements):
    sorted_elements = sorted(elements)
    n = len(sorted_elements)
    subsets = []
    for i in range(2, 2 ** n):
        subset = []
        for j in range(n):
            if i >> j & 1:
                subset.append(sorted_elements[j])
        if subset:
            subsets.append(subset)
    return subsets

